import { Component, Input, OnInit } from '@angular/core';
import { HttpClient, HttpHeaders, HttpParams } from '@angular/common/http';
import { map } from 'rxjs/internal/operators/map';
import { File } from '../files';

@Component({
  selector: 'app-addfile',
  templateUrl: './add-file.component.html',
  styleUrls: ['./add-file.component.css']
})

export class AddComponent implements OnInit{
  @Input() title!: string;
  @Input() file_format!: string;
  @Input()  path!: string;
  @Input() size!: number;
  // files!: File[];

  constructor(private http: HttpClient) { }

  filterTitles(title: string, file_format: string, path: string, size: number){
    const url = 'http://localhost:1234/multimedia.php';
    const data = {action: 'addFile', title: title, file_format: file_format, path: path, size: size};
    console.log(data);
    const httpOptions = {
      headers: new HttpHeaders({
        'Content-Type':  'application/json',
        Authorization: 'my-auth-token'
      })
    };

    const req = this.http.post<File[]>(url, JSON.stringify(data), httpOptions);
    req.subscribe((response: File[]) => {
      console.log( response);
      // this.files = response;
    }, (error) => {
      console.error(error);
    });
  }

  ngOnInit() {
    // this.filterFiles();
  }
}
