import { Component, Input, OnInit } from '@angular/core';
import { HttpClient, HttpHeaders, HttpParams } from '@angular/common/http';
import { FormBuilder, FormControl, FormGroup } from '@angular/forms';
import { ReactiveFormsModule } from '@angular/forms';
import { map } from 'rxjs/internal/operators/map';
import { File } from '../files';

@Component({
  selector: 'app-addfile',
  templateUrl: './addfile.component.html',
  styleUrls: ['./addfile.component.css']
})

export class AddComponent implements OnInit{
  constructor(
    private http: HttpClient,
    private formBuilder: FormBuilder,
    ) { }


  addForm = this.formBuilder.group({
    title: [''],
    file_format: [''],
    path: [''],
    size: [0],
  });

  addFile(){
    const url = 'http://localhost:1234/multimedia.php';
    const data = {action: 'addFile', title: this.addForm['title' as keyof typeof this.addForm], file_format: this.addForm['file_format' as keyof typeof this.addForm], path: this.addForm['path' as keyof typeof this.addForm], size: this.addForm['size' as keyof typeof this.addForm]};
    console.log(data);
    const httpOptions = {
      headers: new HttpHeaders({
        'Content-Type':  'application/json',
        Authorization: 'my-auth-token'
      })
    };

    const req = this.http.post<string>(url, JSON.stringify(data), httpOptions);
    req.subscribe((response: string) => {
      console.log(response);
      console.warn('File added successfully.', this.addForm.value);
      this.addForm.reset();
    }, (error) => {
      console.error(error);
    });
  }

  ngOnInit() {
    // this.filterFiles();
  }
}
